/**
 * Match unsecure pattern web
 * @type {RegExp}
 */
const HTTP_PATTERN = /^http:\/\/|^\/\//

/**
 * Http protocol
 * @type {string}
 */
const HTTP_PROTOCOL = 'http:'

/**
 * Https protocol
 * @type {string}
 */
const HTTPS_PROTOCOL = 'https:'

/**
 * Alpha numeric dictionary
 */
const DICTIONARY = 'abcdefghijklmnopqrstuvwxyz0123456789'

/**
 * Default ZetaPush API URL
 * @access private
 */
export const API_URL = 'https://api.zpush.io/'

/**
 * Force ssl based protocol for network echange
 * Cross Env (Browser/Node) test
 * @access private
 * @type boolean
 */
export const FORCE_HTTPS = typeof location === 'undefined' ? false : location.protocol === HTTPS_PROTOCOL

/**
 * @access private
 * @param {string} apiUrl
 * @return {string}
 */
const normalizeApiUrl = (apiUrl) => {
  const last = apiUrl.charAt(apiUrl.length - 1)
  const SLASH = '/'
  return last === SLASH ? apiUrl : apiUrl + SLASH
}

/**
 * @access private
 * @param {Array<Object>} list
 * @return {Object}
 */
export const shuffle = (list) => {
  const index = Math.floor(Math.random() * list.length)
  return list[index]
}

/**
 * @access private
 * @param {string} url
 * @param {boolean} forceHttps
 * @return {string}
 */
export const getSecureUrl = (url, forceHttps) => {
  return forceHttps ? url.replace(HTTP_PATTERN, `${HTTPS_PROTOCOL}//`) : url
}

/**
 * @access private
 * @param {{apiUrl: string, sandboxId: string, forceHttps: boolean, transports: Transports}} parameters
 * @return {Promise}
 */
export const getServers = ({ apiUrl, sandboxId, forceHttps, transports }) => {
  const normalizedSecuresApiUrl = normalizeApiUrl(getSecureUrl(apiUrl, forceHttps))
  const url = `${normalizedSecuresApiUrl}${sandboxId}`
  const options = { protocol: forceHttps ? HTTPS_PROTOCOL : HTTP_PROTOCOL }
  return transports.fetch(url, options)
    .then((response) => response.json())
    // TODO: Replace by a server side implementation when available
    .then(({ servers }) => servers.map((server) => getSecureUrl(server, forceHttps)))
}

/**
 * @access private
 * @param Class Derived
 * @param Class Parent
 * @return {boolean}
 */
export const isDerivedOf = (Derived, Parent) => {
  let prototype = Object.getPrototypeOf(Derived)
  let is = false
  while (!(is || prototype === null)) {
    is = prototype === Parent
    prototype = Object.getPrototypeOf(prototype)
  }
  return is
}

/**
 * Get random id
 * @return {string}
 */
export const uuid = (entropy = 7, dictionary = DICTIONARY) => Array.from(Array(entropy)).reduce((previous) => {
  const next = dictionary.charAt(Math.floor(Math.random() * dictionary.length))
  return `${previous}${next}`
}, '')

import { platformStorage } from './storage'

/**
 * @type {string}
 */
export const ZETAPUSH_SESSION_KEY = 'zetapush.token'

/**
 * Provide abstraction for token persistence
 * @access protected
 */
export class SessionPersistenceStrategy {
  /**
   * @param {{sandboxId: string, storage: DOMStorage}} parameters
   */
  constructor({ sandboxId, storage = platformStorage } = {}) {
    /**
     * @access private
     * @type {string}
     */
    this.key = `${ZETAPUSH_SESSION_KEY}.${sandboxId}`
    /**
     * @access private
     * @type {DOMStorage}
     */
    this.storage = storage
  }
  /**
   * @return {string} session The stored session
   */
  get() {
    const { key, storage } = this
    const json = storage.getItem(key) || '{}'
    let session = {}
    try {
      session = JSON.parse(json)
    } catch (e) { }
    return session
  }
  /**
   * @param {Object} session The session to store
   */
  set(session = {}) {
    const { key, storage } = this
    const json = JSON.stringify(session)
    try {
      storage.setItem(key, json)
    } catch (e) { }
    return session
  }
}

/**
 * Provide fallback for DOMStorage
 * @access protected
 */
class MemoryStorage {
  constructor() {
    this._map = new Map()
  }
  getItem(key) {
    return this._map.get(key)
  }
  setItem(key, value) {
    return this._map.get(key)
  }
  removeItem(key) {
    this._map.delete(key)
  }
  clear() {
    this._map = new Map()
  }
  key(n) {
    return Array.from(this._map.keys())[n]
  }
  get length() {
    return this._map.size
  }
}

/**
 * @type {Storage}
 * @access protected
 */
export const platformStorage = typeof localStorage === 'undefined' ? new MemoryStorage() : localStorage

